"use strict";
var ticksAtEpoch = 621355968000000000;
var ticksPerMillisecond = 10000;
/**
 * A function to convert JavaScript Unix epoch date to C# ticks.
 */
function epochDateToTicks(millisecondSinceEpoch) {
    return ticksAtEpoch + (millisecondSinceEpoch * ticksPerMillisecond);
}
exports.epochDateToTicks = epochDateToTicks;
/**
 * A function to convert C# ticks to JavaScript Unix epoch date.
 * Warning: there is some loss of precision due to ticks being more precise
 * than milliseconds as well as with the JavaScript number type.
 * For example, 621355968015123456 ticks will become 621355968015123500
 * when represented via a double precision 64 bit float in JavaScript's memory.
 * Then when this is converted to epoch date and back again it becomes
 *  621355968015120000 due to rounding. Since we only need MS precision we are ok.
 */
function ticksToEpochDate(ticks) {
    return Math.round((ticks - ticksAtEpoch) / ticksPerMillisecond);
}
exports.ticksToEpochDate = ticksToEpochDate;
