"use strict";
exports.emptyGuid = "00000000-0000-0000-0000-000000000000";
function stripBrackets(input) {
    return input.replace(/[{}]/g, "").toLowerCase();
}
exports.stripBrackets = stripBrackets;
function addBrackets(input) {
    return "{" + input.toUpperCase() + "}";
}
exports.addBrackets = addBrackets;
function isGuid(input) {
    var guidReg = /[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}/;
    return guidReg.test(input);
}
exports.isGuid = isGuid;
