"use strict";
var os = require("os");
var OSPropertyProvider = (function () {
    function OSPropertyProvider() {
    }
    /**
     * Add shared properties to the provided context.
     * @param context Context to add shared property
     */
    OSPropertyProvider.prototype.addSharedProperties = function (context) {
        context.setSharedProperty("VS.Core.OS.Version", os.release());
        context.setSharedProperty("VS.Core.OS.Type", os.type());
        return Promise.resolve(null);
    };
    /**
     * Posts properties to the provided context.
     * @param context Context to post property
     */
    OSPropertyProvider.prototype.postProperties = function (context) {
        return Promise.resolve(null);
    };
    return OSPropertyProvider;
}());
exports.OSPropertyProvider = OSPropertyProvider;
